"use strict";const isMobile={Android:function(){return navigator.userAgent.match(/Android/i)},BlackBerry:function(){return navigator.userAgent.match(/BlackBerry/i)},iOS:function(){return navigator.userAgent.match(/iPhone|iPad|iPod/i)},Opera:function(){return navigator.userAgent.match(/Opera Mini/i)},Windows:function(){return navigator.userAgent.match(/IEMobile/i)},any:function(){return isMobile.Android()||isMobile.BlackBerry()||isMobile.iOS()||isMobile.Opera()||isMobile.Windows()}};if(isMobile.any()){document.body.classList.add("_touch");let t=document.querySelectorAll(".menu__arrow");if(0<t.length)for(let e=0;e<t.length;e++){const c=t[e];c.addEventListener("click",function(e){c.parentElement.classList.toggle("_active")})}}else document.body.classList.add("_pc");const menuLinks=document.querySelectorAll(".menu__link[data-goto]");function onMenuLinkClick(e){var t=e.target;if(t.dataset.goto&&document.querySelector(t.dataset.goto)){const n=document.querySelector(t.dataset.goto);t=n.getBoundingClientRect().top+pageYOffset-document.querySelector("header").offsetHeight;window.scrollTo({top:t,behavior:"smooth"}),e.preventDefault()}}0<menuLinks.length&&menuLinks.forEach(e=>{e.addEventListener("click",onMenuLinkClick)});const iconMenu=document.querySelector(".menu__icon");if(iconMenu){const j=document.querySelector(".menu__body");iconMenu.addEventListener("click",function(e){document.body.classList.toggle("_lock"),iconMenu.classList.toggle("_active"),j.classList.toggle("_active")})}